import { GlobalContext } from "../context/GlobalState";
import { useContext } from "react";

function IncomeExpense() {
    const {transactions} = useContext(GlobalContext)
    const income = transactions.filter(item=>item.amount>=0).reduce((acc,i)=>acc+=i)
    const expense = transactions.filter(item=>item.amount<0).reduce((acc,i)=>acc+=i)
    return (
        <div className="inc-exp-container">
            <div>
                <h4>Income</h4>
                <p id="money-plus" className="money plus">+$0.00</p>
            </div>
            <div>
                <h4>Expense</h4>
                <p id="money-minus" className="money minus">-$0.00</p>
            </div>
        </div>
    );
}

export default IncomeExpense;
